import {Component, OnInit} from '@angular/core';
import {Category} from "../models";
import {ActivatedRoute} from "@angular/router";
import { Router } from '@angular/router';
import {CategoryService} from "../category.service";
@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit{
  categories: Category[];
  SearchTitle: string;
  constructor(private router: Router, private categoryService: CategoryService) {
    this.categories = [];
    this.SearchTitle = "";

  }

  ResetTitle(){
    this.SearchTitle = "";
  }

  redirectTo(uri:string){
    this.router.navigateByUrl('/', {skipLocationChange: true}).then(()=>{
      this.router.navigate([uri, this.SearchTitle])
      this.ResetTitle();
  });
  }

  ngOnInit(): void {
    this.getCategories()
  }

  getCategories(){
    this.categoryService.getCategories().subscribe((categories) => {
      this.categories = categories;
    });
  }
}
<header class="header">
  <div class="logo" routerLink="/home" >
    <h1>Araba.kz</h1>
  </div>
  <div class="center">
    <span routerLink="/cars">Vehicles</span>
    <span routerLink="/charging">Charging</span>
    <span routerLink="/article">Discover</span>
    <span routerLink="/accessories">Accessories</span>
    <span>All</span>
  </div>
  <div class="menu">
    <i class="fas fa-user" style="color: #000000;" routerLink="/account" [routerLinkActive]="'menu-item-activated'"></i>
    <i class="fas fa-heart" style="color: #000000;" routerLink="/favorites" [routerLinkActive]="'menu-item-activated'"></i>
    <i class="fas fa-shopping-cart" style="color: #000000;" routerLink="/purchases" [routerLinkActive]="'menu-item-activated'"></i>
  </div>
</header>
