import { Component } from '@angular/core';
import {AuthService} from "../auth.service";
import {error} from "@angular/compiler-cli/src/transformers/util";
import {Router} from "@angular/router";
export interface User{
  username: string,
  password: string
}
@Component({
  selector: 'app-authorization',
  templateUrl: './authorization.component.html',
  styleUrls: ['./authorization.component.css']
})


export class AuthorizationComponent {
  username: string;
  password: string;
  invalidData: boolean;
  constructor(private authService: AuthService, private router: Router) {
    this.password = "";
    this.username = "";
    this.invalidData = false;
  }

  login(){
      let user = {
      username: this.username,
      password: this.password
    }
    this.authService.login(user).subscribe(result => {
      localStorage.setItem('token', result.token);
      this.router.navigate(['../account']);
    }, error => {
      this.invalidData = true;
    })
  }
}
<div class="container">
<div class="authorization">
  <h1 class="authorization-title">Authorization</h1>
  <form class="authorization-data" (ngSubmit)="login()">
    <input type="text" placeholder="Email or Phone" class="authorization-login authorization-input" name="username" [(ngModel)]="username" autocomplete="off">
    <input type="password" placeholder="Password" class="authorization-password authorization-input"  name="password" [(ngModel)]="password" autocomplete="off">
    <input type="submit" value="Sign In" class="authorization-enter authorization-button">
  </form>
  <p class="authorization-register">Don't have an account? <a [routerLink]="['/registration']">Register</a></p>
</div>
</div>
